// รับ items จากโหนดก่อนหน้า แล้วเติมฟิลด์ inputPath ให้เรียบร้อย
// กติกา: ใช้ filePath ถ้ามี; ถ้าไม่มี ให้ประกอบจาก directory + fileName; ถ้าไม่มี directory ก็ใช้ fileName
return items.map(({ json, binary }) => {
  const filePath =
    json.filePath
      ? json.filePath
      : (json.directory
          ? `${json.directory.replace(/\/$/, '')}/${json.fileName}`
          : json.fileName);

  return {
    json: {
      ...json,
      inputPath: filePath,
    },
    binary, // เผื่ออยากพก binary ต่อไปด้วย
  };
});

const raw = $json["text"];

// 1. ลบ ```json และ ``` ที่ LLM อาจใส่มา
const cleaned = raw.replace(/```json\n?|```/g, "").trim();

let parsed;
try {
  // 2. แปลงเป็น object
  parsed = JSON.parse(cleaned);
} catch (err) {
  throw new Error("JSON parsing failed: " + err.message + "\n\nRaw text:\n" + cleaned);
}

// 3. หาก contact เป็น object แยก field ออกมา
const contact = parsed.contact || {};

return {
  book_id: parsed.book_id || "",
  date: parsed.date || "",
  subject: parsed.subject || "",
  to: parsed.to || "",
  attach: parsed.attach || "",
  detail: parsed.detail || "",
  signed_by: parsed.signed_by || "",
  signed_by2: parsed.signed_by2 || "",
  contact_phone: contact.phone || "",
  contact_email: contact.email || "",
  contact_fax: contact.fax || "",
  download_url: parsed.download_url || ""
};
