const items = [];
const inputData = $input.all();

for (let i = 0; i < inputData.length; i++) {
  const row = inputData[i].json;

  // Validate required fields
  if (row.symbol && row.upper_limit && row.lower_limit) {
    items.push({
      json: {
        symbol: row.symbol,
        upper_limit: parseFloat(row.upper_limit),
        lower_limit: parseFloat(row.lower_limit),
        direction: row.direction || 'both',
        cooldown_minutes: parseInt(row.cooldown_minutes) || 15,
        last_alert_price: parseFloat(row.last_alert_price) || 0,
        last_alert_time: row.last_alert_time || ''
      }
    });
  }
}

return items;

const watchlist = $('Parse Watchlist Data').first().json;

const symbol = watchlist.symbol;
const upperLimit = parseFloat(watchlist.upper_limit);
const lowerLimit = parseFloat(watchlist.lower_limit);

const values = $input.first().json.values;

let alerts = [];

for (let i = 0; i < Math.min(20, values.length); i++) {
  const datetime = values[i].datetime;
  const closePrice = parseFloat(values[i].close);

  if (closePrice >= upperLimit) {
    alerts.push({
      symbol: symbol,
      status: 'UP',
      price: closePrice,
      limit: upperLimit,
      datetime: datetime,
      message: `${datetime} - ${symbol} crossed UP limit. Current Price: ${closePrice}`
    });
  } else if (closePrice <= lowerLimit) {
    alerts.push({
      symbol: symbol,
      status: 'DOWN',
      price: closePrice,
      limit: lowerLimit,
      datetime: datetime,
      message: `${datetime} - ${symbol} crossed DOWN limit. Current Price: ${closePrice}`
    });
  }
}

return alerts.length ? alerts.map(alert => ({ json: alert })) : [{ json: { message: 'No alerts' } }];
