const verifyToken = query['hub.verify_token'];
const challenge = query['hub.challenge'];
const mode = query['hub.mode'];

// Verify the token matches what you sent (yours is "lakshit")
if (verifyToken === 'shayan_ali_bakhsh' && mode === 'subscribe') {
  return [{
    json: {
      challenge: challenge,
      status: 200,
      message: 'Verification successful'
    }
  }];
} else {
  return [{
    json: {
