const data = $getWorkflowStaticData('global'); // Correct static data helper

// Get the first (latest) item only
const item = $input.first().json;
const mediaGroup = item["media:group"] || {};
const rawThumb = mediaGroup["media:thumbnail"]?.[0]?.["$"]?.url || null;
const thumbnail = rawThumb ? rawThumb.replace("hqdefault", "mqdefault") : null;

const latest = {
  title: item.title || null,
  link: item.link || null,
  author: item.author || null,
  publishedAt: item.pubDate || null,
  videoId: (item.id || "").replace("yt:video:", ""),
  description: mediaGroup["media:description"]?.[0] || null,
  thumbnail,
  views: mediaGroup["media:community"]?.[0]?.["media:statistics"]?.[0]?.["$"]?.views || null,
  rating: mediaGroup["media:community"]?.[0]?.["media:starRating"]?.[0]?.["$"]?.average || null,
  ratingCount: mediaGroup["media:community"]?.[0]?.["media:starRating"]?.[0]?.["$"]?.count || null,
};

// Compare with last saved videoId
if (!data.lastVideoId) {
  data.lastVideoId = latest.videoId;
  return [{ json: { new: true, item: latest } }];
}

if (data.lastVideoId !== latest.videoId) {
  data.lastVideoId = latest.videoId;
  return [{ json: { new: true, item: latest } }];
}

return [{ json: { new: false, item: latest } }];

