const unstructuredJson = $input.first().json.content?.parts?.[0]?.text || "";

// Clean up markdown fences if they exist
const cleaned = unstructuredJson
  .replace(/^```json\s*/i, "")
  .replace(/```$/i, "")
  .trim();

let finalJson;

try {
  finalJson = JSON.parse(cleaned);
} catch (error) {
  // fallback in case parsing fails
  finalJson = {
    short_description: null,
    post_text: null,
    error: "Failed to parse AI response",
    raw: unstructuredJson
  };
}

return finalJson;

