let retry = 0;

while (retry < 4) {
  await new Promise(resolve => setTimeout(resolve, 5000));

  const apiCall = await this.helpers.httpRequest({
      method: 'GET',
      url: `https://socialbu.com/api/v1/upload_media/status`,
      qs: {
          key: $input.first().json.key,
      },
      headers: {
          Authorization: `Bearer ${$('SocialBu Access Token').first().json.authToken}`
      },
      json: true,
  });

  // Check success
  if (apiCall?.success === true) {
      return [{ json: { success: true, data: apiCall } }];
  }

  // Increment retry
  retry++;
}

// After all retries failed
return [{ json: { success: false } }];

const allData = $input.all();

// Format JS Date to "YYYY-MM-DD HH:mm:ss" (UTC)
function formatDateToUTCString(date) {
  const pad = (n) => (n < 10 ? '0' + n : n);

  return (
    date.getUTCFullYear() + '-' +
    pad(date.getUTCMonth() + 1) + '-' +
    pad(date.getUTCDate()) + ' ' +
    pad(date.getUTCHours()) + ':' +
    pad(date.getUTCMinutes()) + ':' +
    pad(date.getUTCSeconds())
  );
}

// Publish time: current UTC + 1 minute
const publishAt = formatDateToUTCString(new Date(Date.now() + 60 * 1000));

// Extract upload_token from the first item
const uploadToken = $input.first().json.data.upload_token;

// Collect active accounts that support PNG
const accountsThatAcceptPNG = allData
  .slice(1) // skip first item (upload info)
  .filter(item => item.json.active && item.json.attachment_types?.includes("png"))
  .map(item => item.json.id);

// Final output
return {
  json: {
    accounts: accountsThatAcceptPNG,
    attachment: uploadToken,
    publish_at: publishAt,
  }
};
