// Add a new field called 'myNewField' to the JSON of the item
function buildPattern(word) {
  return word
    .toLowerCase()
    .trim()
    // 处理连字符和空格变体：off-the-shoulder -> off[-\s]?the[-\s]?shoulder
    .replace(/[- ]/g, '[\\s-]?') 
    // 转义特殊字符
    .replace(/[.*+?^${}()|[\]\\]/g, '\\$&');
}

const rulesOrder = [
    'brand',       
    'demographic',
    'fabric', 
    'design',
    'holiday',
    'scenario',
    'size',
    'location'     
  ];
const keyword = $input.item.json.Keyword.toLowerCase();
let matchedRule = '';
for (const ruleType of rulesOrder) {
    const words = $input.item.json[ruleType] || [];
    if (words.length === 0) continue;

    // 构建正则表达式模式
    const patterns = words.map(word => 
      `\\b${buildPattern(word)}\\b` // 单词边界匹配
    );

    const regex = new RegExp(patterns.join('|'), 'i');
    if (regex.test(keyword)) {
      matchedRule = ruleType;
      break; // 发现违规立即终止
    }
  }

  return {
    Keyword: keyword,
    filter_rule: matchedRule
  };

