return items.map(item => {
  const j = item.json || {};
  const original = j.website || null;

  let website_root = null;
  let domain = null;
  let domain_strategy = 'none';

  if (original && typeof original === 'string') {
    let urlStr = original.trim();

    // Add protocol if missing
    if (!/^https?:\/\//i.test(urlStr)) {
      urlStr = 'https://' + urlStr;
    }

    try {
      const urlObj = new URL(urlStr);
      website_root = `${urlObj.protocol}//${urlObj.host}`;
      domain = urlObj.host.toLowerCase();
      domain_strategy = 'site_host';
    } catch (e) {
      // fallback if URL is malformed
      website_root = urlStr.split('?')[0];
      const match = website_root.match(/^https?:\/\/([^\/]+)/i);
      if (match) {
        domain = match[1].toLowerCase();
        domain_strategy = 'site_host_fallback';
      }
    }
  }

  // Remove "www." prefix
  if (domain && domain.startsWith('www.')) {
    domain = domain.slice(4);
  }

  // Exclude social networks
  const SOCIAL_HOSTS = [
    'facebook.com', 'instagram.com', 'linktr.ee', 'twitter.com', 'google.com', 'youtube.com'
  ];
  if (domain && SOCIAL_HOSTS.some(h => domain.endsWith(h))) {
    domain = null;
    domain_strategy = 'social';
  }

  // Output
  j.website_root = website_root;
  j.domain = domain;
  j.domain_strategy = domain_strategy;
  if (website_root) j.website = website_root;

  item.json = j;
  return item;
});

const allPlaceIds = $('Get all the recorded placeIds').all().map(item => item.json.placeId);
const scrapedItems = $('Clean Data').all().map(item => item.json);

// keep only items with a new placeId
const filtered = scrapedItems.filter(item => item.placeId && !allPlaceIds.includes(item.placeId));

// return each as its own item
return filtered.map(item => ({ json: item }));
