const data = items[0].json.body;

const category = {
  id: data.id,
  name: data.name,
  slug: data.slug,
  description: data.description,
  parent: data.parent,
  display_type: data.args?.display_type,
  thumbnail_id: data.args?.product_cat_thumbnail_id
};

return [
  {
    json: category
  }
];

const items = $input.all();

const updatedItems = items.map((item) => {
  const billing = item?.json?.billing || {};

  // number extract & clean
  const waNo = billing.phone;
  const waNoStr = typeof waNo === 'string' ? waNo : (waNo !== undefined && waNo !== null ? String(waNo) : "");
  const cleanedNumber = waNoStr.replace(/\D/g, "");

  // Email extract
  const email = billing.email || item?.json?.email || "";

  // Full address generate
  const addressParts = [
    billing.address_1,
    billing.address_2,
    billing.city,
    billing.state,
    billing.postcode,
    billing.country
  ].filter(Boolean); // remove empty values
  const fullAddress = addressParts.join(', ');

  // Output format
  return {
    json: {
      number: cleanedNumber,
      email: email,
      address: fullAddress,
      name: `${billing.first_name || ''} ${billing.last_name || ''}`.trim()
    }
  };
});

return updatedItems;
