let results = [];
const emailData = $('Email_Trigger').item;

if (emailData.binary) {
    for (const key of Object.keys(emailData.binary)) {
        const attachment = emailData.binary[key];
        const mimeType = attachment.mimeType;

        // Filter for specific file types (PDF, XML, JPG)
        if (mimeType === 'application/pdf' || 
            mimeType === 'text/xml' || 
            mimeType === 'application/xml' ||
            mimeType === 'image/jpeg') {
            results.push({
                json: {
                    fileName: attachment.fileName,
                    mimeType: attachment.mimeType
                },
                binary: {
                    data: attachment
                }
            });
        }
    }
}

return results;

