fieldValues.fieldValue:
={{ JSON.stringify(
  (() => {
    const prev = $item(0).$node["Get many rows"].json.metadata || {};
    let enriched = {};
    try {
      const raw = $('Metadata Obtention').item.json.content?.parts?.[0]?.text || "{}";
      enriched = JSON.parse(raw);
    } catch(_) {}
    return {
      ...prev,
      ...enriched,
      processed: true,
      enricher_version: "v2-generic-2025-08",
      enriched_at: new Date().toISOString(),
    };
  })()
) }}
