// Get the array of words from the input JSON provided by ElevenLabs
// It assumes the input data is in the format $json.words or $items[0].json.words
const words = $json.words;

// If there are no words, return an empty transcript
if (!words || words.length === 0) {
  return { json: { transcript: "" } };
}

let transcript = "";
let currentSpeaker = null;
let currentLine = "";

// Loop through each word object provided by the API
for (const word of words) {
  const speakerId = word.speaker_id;

  // Check if this is the very first word
  if (currentSpeaker === null) {
    currentSpeaker = speakerId;
    // Format the speaker ID for better readability (e.g., 'speaker_0' becomes 'Speaker 0')
    const formattedSpeaker = `Speaker ${speakerId.split('_')[1]}`;
    currentLine = `${formattedSpeaker}: ${word.text}`;
    continue;
  }

  // If the speaker changes, add the completed line to the transcript and start a new one
  if (speakerId !== currentSpeaker) {
    transcript += currentLine.trim() + "\n"; // Add the previous line and a newline
    currentSpeaker = speakerId;
    const formattedSpeaker = `Speaker ${speakerId.split('_')[1]}`;
    currentLine = `${formattedSpeaker}: ${word.text}`;
  } else {
    // If the same speaker continues, just add their word to the current line
    // The API includes spacing objects, so we just append the text
    currentLine += word.text;
  }
}

// Add the very last line to the transcript
transcript += currentLine.trim();

// Return the final formatted transcript in a new n8n item
return {
  json: {
    formattedTranscript: transcript
  }
};
