if (!match) {
  throw new Error("No JSON object found in the input text.");
}

let parsed;
try {
  parsed = JSON.parse(match[0]);
} catch (e) {
  throw new Error("Failed to parse JSON: " + e.message);
}

// Return a single object (not an array!) in 'Run Once for Each Item' mode
return { json: parsed };

// Try to get the input text from common fields
const input =
  $json.text ||
  $json.data ||
  $json.body ||
  $json.content ||
  $input.item.json.text ||
  $input.item.json.content ||
  "";

// Regex to match the first JSON object in the text (non-greedy)
const match = input.match(/{[\s\S]*?}/);

if (!match) {
  throw new Error("No JSON object found in the input text.");
}

let jsonString = match[0];

// Clean up: Remove unescaped line breaks and fix bad escape sequences
jsonString = jsonString
  // Replace unescaped line breaks inside JSON strings with \n
  .replace(/([^\\])\\n/g, '$1\\\\n') // double-escape existing \n
  .replace(/\\(?!["\\/bfnrtu])/g, '\\\\') // escape lone backslashes
  .replace(/[\r\n]+/g, ' '); // replace all CR/LF with space

let parsed;
try {
  parsed = JSON.parse(jsonString);
} catch (e) {
  throw new Error("Failed to parse JSON: " + e.message + "\nCleaned JSON: " + jsonString);
}

// Only return title and summary
return {
  title: parsed.title,
  summary: parsed.summary
};

