// Zählt die Wörter im content-Feld und fügt das Ergebnis als word_count hinzu
const content = $input.item.json.content || "";
const wordCount = content
  .replace(/<[^>]*>/g, ' ') // HTML-Tags entfernen, falls vorhanden
  .trim()
  .split(/\s+/)
  .filter(Boolean)
  .length;

$input.item.json.word_count = wordCount;
return $input.item;
