// Pass all Excel data through, normalize dates, and clean up text
const results = [];

// Excel date formatting (simple)
function fixExcelDate(excelDate) {
  if (!excelDate || isNaN(excelDate)) return null;
  const date = new Date((excelDate - 25569) * 86400 * 1000);
  return date.toISOString().split('T')[0];
}

for (const item of $input.all()) {
  const cleanedItem = { ...item.json };

  // Fix Excel dates - replace originals to avoid duplicates
  if (cleanedItem['Creation Date']) {
    cleanedItem['Creation Date'] = fixExcelDate(cleanedItem['Creation Date']);
  }
  if (cleanedItem['Closed on']) {
    cleanedItem['Closed on'] = fixExcelDate(cleanedItem['Closed on']);
  }

  // Clean Question field
  if (cleanedItem['Question']) {
    cleanedItem['Question_clean'] = cleanedItem['Question']
      .toString()
      .replace(/<[^>]*>/g, '')     // remove HTML tags
      .replace(/&nbsp;/g, ' ')     // replace &nbsp; with space
      .replace(/&[^;]+;/g, ' ')    // replace other HTML entities
      .replace(/"\s"/g, '')        // remove " "
      .replace(/\s+/g, ' ')        // collapse multiple spaces
      .trim();                     // trim edges
  }

  // Clean Answer field - FIXED: was processing Question instead of Answer
  if (cleanedItem['Answer']) {
    cleanedItem['Answer_clean'] = cleanedItem['Answer']
      .toString()
      .replace(/<[^>]*>/g, '')     // remove HTML tags
      .replace(/&nbsp;/g, ' ')     // replace &nbsp; with space
      .replace(/&[^;]+;/g, ' ')    // replace other HTML entities
      .replace(/"\s"/g, '')        // remove " "
      .replace(/\s+/g, ' ')        // collapse multiple spaces
      .trim();                     // trim edges
  }

  // Add flags - FIXED: boolean logic and field names
  cleanedItem['has_Question'] = (cleanedItem['Question_clean'] && cleanedItem['Question_clean'].length > 10);
  cleanedItem['has_Answer'] = (cleanedItem['Answer_clean'] && cleanedItem['Answer_clean'].length > 10);

  results.push({
    json: cleanedItem
  });
}

return results;

// Process only Customer Question field
const item = $input.first().json;

// Check whether we need to process Customer Question
if (!item.has_Question || !item.Question_clean) {
  return [];
}

// Just send the text to OpenAI ‚Äì no extra metadata
return [{
  json: {
    record_id: item.id || item.UUID,
     text_to_embed: item.Question_clean,
    original_item: item
  }
}];

// Process only Customer Answer field
const item = $input.first().json;

// Check whether we need to process Customer Answer
if (!item.has_Answer || !item.Answer_clean) {
  return [];
}

// Just send the text to OpenAI ‚Äì no extra metadata
return [{
  json: {
    text_to_embed: item.Answer_clean
  }
}];

const inputs = $input.all();

let originalData = null;
let QuestionEmbedding = null;
let AnswerEmbedding = null;

for (const input of inputs) {
  // Original data from Loop Over Items
  if (input.json.UUID && !input.json.data) {
    originalData = input.json;
  }
  // Direct OpenAI HTTP response
  else if (input.json.data && input.json.data[0] && input.json.data[0].embedding) {
    // Here you must distinguish between Question and Answer embeddings
    if (!AnswerEmbedding) {
      QuestionEmbedding = input.json.data[0].embedding;
    } else {
      AnswerEmbedding = input.json.data[0].embedding;
    }
  }
}

return [{
  json: {
    id: originalData.UUID,
    ...originalData,
    Question_embedding: QuestionEmbedding,
    Answer_embedding: AnswerEmbedding
  }
}];
