let html = $('Get page').item.json.data;
let baseDomain = $('Limit').item.json.domain 

// 0. Vibe coding starts here:
const links = new Set();
const scriptTextSnippets = [];
const emails = new Set();

// 1. Extract <a href="...">
html.replace(/<a\b[^>]*href=["']([^"']+)["']/gi, (_, href) => {
  href = href.trim();
  if (href.startsWith('/') && href != '/' && !href.includes('login') && !href.includes('signup') && !href.includes('signin') && !href.includes('password')) {
    links.add(baseDomain + href);
  } else if (/^https?:\/\/.+\.pdf$/i.test(href)) {
    links.add(href);
  }
  return '';
});

// 2. Extract url: "/..." and full PDF URLs from JS blocks
html.replace(/url\s*:\s*["']([^"']+)["']/gi, (_, url) => {
  url = url.trim();
  if (url.startsWith('/') && url != '/' && !url.includes('login') && !url.includes('signup') && !url.includes('signin') && !url.includes('password')) {
    links.add(baseDomain + url);
  } else if (/^https?:\/\/.+\.pdf$/i.test(url)) {
    links.add(url);
  }
  return '';
});

// 3. Extract content="/..." or content="https://...pdf"
html.replace(/content\s*=\s*["']([^"']+)["']/gi, (_, contentUrl) => {
  contentUrl = contentUrl.trim();
  if (contentUrl.startsWith('/') && contentUrl != '/' && !contentUrl.includes('login') && !contentUrl.includes('signup') && !contentUrl.includes('signin') && !contentUrl.includes('password')) {
    links.add(baseDomain + contentUrl);
  } else if (/^https?:\/\/.+\.pdf$/i.test(contentUrl)) {
    links.add(contentUrl);
  }
  return '';
});

// 4. Extract text from <script> blocks with `text: "..."` only
html.replace(/<script[^>]*>([\s\S]*?)<\/script>/gi, (_, scriptContent) => {
  const matches = scriptContent.match(/text\s*:\s*"([^"]+)"/g);
  if (matches) {
    matches.forEach(match => {
      const value = match.match(/text\s*:\s*"([^"]+)"/);
      if (value && value[1]) {
        scriptTextSnippets.push(value[1].trim());
      }
    });
  }

  // Also extract emails from inside script
  const emailMatches = scriptContent.match(/[a-zA-Z0-9._%+-]+@[a-zA-Z0-9.-]+\.[a-z]{2,}/gi);
  if (emailMatches) {
    emailMatches.forEach(email => emails.add(email.toLowerCase()));
  }
  return '';
});

// 5. Clean visible HTML text (remove unwanted tags)
let visibleHtml = html
  .replace(/<script[^>]*>[\s\S]*?<\/script>/gi, '')
  .replace(/<style[^>]*>[\s\S]*?<\/style>/gi, '')
  .replace(/<head[^>]*>[\s\S]*?<\/head>/gi, '')
  .replace(/<!--[\s\S]*?-->/g, '')
  .replace(/<\/?[^>]+>/g, ' ');

// 6. Normalize visible text
const visibleText = visibleHtml.replace(/\s+/g, ' ').trim();

// 7. Merge visible text + extracted script text
const fullText = (visibleText + ' ' + scriptTextSnippets.join(' ')).replace(/\s+/g, ' ').trim();

// 8. Extract emails from visible text
const emailMatches = visibleText.match(/[a-zA-Z0-9._%+-]+@[a-zA-Z0-9.-]+\.[a-z]{2,}/gi);
if (emailMatches) {
  emailMatches.forEach(email => emails.add(email.toLowerCase()));
}

// 9. Final output
return {
  "text": fullText,
  "links": Array.from(links),
  "emails": Array.from(emails)
};
