// This is what your Code node would generate for ONE question
const questionRequests = [];

// Assuming 'questions' is the array of questions
for (const item of $('Extract JSON').first().json.output.Questions) { // $input.first().json.output.Questions) {
  const options = [];
  // Assuming item.json.Option1, item.json.Option2, etc. from Google Sheet
  if (item.options[0]) options.push({ value: item.options[0] });
  if (item.options[1]) options.push({ value: item.options[1] });
  if (item.options[2]) options.push({ value: item.options[2] });

  const correctAnswers = [{ value: item.answer }]; // Assuming item.json.CorrectAnswer

  questionRequests.push(
    {"data":[
      {
      "updateSettings": {
        "settings": {
          "quizSettings": {
            "isQuiz": true
          }
        },
        "updateMask": "quizSettings.isQuiz"
      }
      },
      {"createItem": {
      "item": {
        "title": item.question, // Question text from Google Sheet
        "questionItem": {
          "question": {
            "choiceQuestion": {
              "options": options,
              "shuffle": true, // Optional: Randomize order of options
              "type": "RADIO"
            },
            "grading": {
               // Points for the question
              "correctAnswers": {
                "answers": correctAnswers 
              },
              "pointValue": 1,
              "whenRight": {
                  "text": "Great job! That's correct."
                },
              "whenWrong": {
                  "text": `Not quite. The correct answer was '${item.answer}'.`
                }

            }
          }
        }
      },
      "location": {
        "index": questionRequests.length // Adds questions in order
      }
    }
  }]});
}


return questionRequests;
