parameters.jsCode:
const items = $input.all().map((item) => item.json);
const today = new Date().toISOString().slice(0, 10);
const filteredItems = items.filter((item) => item["Crawled Date"] === today);

const groupedItems = filteredItems.reduce((acc, item) => {
  acc[item.Company] = acc[item.Company] || [];
  acc[item.Company].push(item);
  return acc;
}, {});

let html = "";

for (const company in groupedItems) {
  html += `<h2>${company}</h2>`;
  html += `<table style="border-collapse: collapse;">`;
  html += `<tr>
    <th style="border: 1px solid #000;">Crawled Date</th>
    <th style="border: 1px solid #000;">Page Type</th>
    <th style="border: 1px solid #000;">Title</th>
    <th style="border: 1px solid #000;">Author</th>
    <th style="border: 1px solid #000;">Published Date</th>
    <th style="border: 1px solid #000;">URL</th>
    <th style="border: 1px solid #000;">Summary</th>
  </tr>`;
  for (const row of groupedItems[company]) {
    html += `<tr>
      <td style="border: 1px solid #000;">${row["Crawled Date"]}</td>
      <td style="border: 1px solid #000;">${row["Page Type"]}</td>
      <td style="border: 1px solid #000;">${row.Title}</td>
      <td style="border: 1px solid #000;">${row.Author}</td>
      <td style="border: 1px solid #000;">${row["Published Date"]}</td>
      <td style="border: 1px solid #000;">${row.URL}</td>
      <td style="border: 1px solid #000;">${row.Summary}</td>
    </tr>`;
  }
  html += `</table>`;
}

return { html };
