let grupos = {};

for (const item of items) {
  const grupo = item.json["RemoteJid Grupo"];  // com espa√ßo no nome

  if (!grupos[grupo]) grupos[grupo] = [];

  grupos[grupo].push(item.json.mensagem); // campo "mensagem" vem limpo mesmo
}

let resultados = [];

for (const grupoId in grupos) {
  resultados.push({
    json: {
      grupo: grupoId,
      mensagens: grupos[grupoId].join('\n')
    }
  });
}

return resultados;
