const steps = items[0].json.routes[0].steps;

const orderedJobs = steps
  .filter(step => step.type === "job")
  .map((step, index) => ({
    id: step.job,
    sequence: index + 1
  }));

return orderedJobs.map(job => ({ json: job }));

// This Function node transforms store pickup data into the OpenRouteService optimization payload

const inputData = items.map(item => item.json);

// Build the jobs array
const jobs = inputData.map((entry, index) => ({
  id: index + 1,
  location: [entry.longitude, entry.latitude]
}));

// Take the second item as the starting location (adjust index if needed)
const vehicleStart = [inputData[1].longitude, inputData[1].latitude];

const vehicles = [
  {
    id: 1,
    start: vehicleStart,
    capacity: [100],
    profile: "driving-hgv"
  }
];

return [
  {
    json: {
      jobs,
      vehicles
    }
  }
];

// Sort and format the pickup sequence
const sorted = items.sort((a, b) => a.json.sequence - b.json.sequence);

const pickupList = sorted.map((item, index) => `
  <li><strong>Stop ${index + 1}</strong>: ${item.json.store_id}<br>
  Address: ${item.json.address}<br>
  Coordinates: ${item.json.gps_coordinates}<br>
  Scheduled Time: ${item.json.collection_time}</li>
`).join('');

const htmlReply = `
<p>We have scheduled your multi-stop pickup request (ID: <strong>${sorted[0].json.collection_request_id}</strong>) for <strong>${sorted[0].json.collection_date}</strong> at <strong>${sorted[0].json.collection_time}</strong>.</p>

<p>Below is the confirmed pickup sequence:</p>
<ul>${pickupList}</ul>

<p>The total estimated driving duration is <strong>${sorted[0].json.total_duration} minutes</strong>.</p>
`;

return [{ json: { formatted_reply: htmlReply } }];
