// This node is the brain of the whole operation.
// It uses Static Data, which is like a shared whiteboard for the workflow.

// Get the shared whiteboard data.
const staticData = $getWorkflowStaticData('global');
if (!staticData.projects) {
  staticData.projects = {};
}

// Get the status from the incoming item.
const status = $json.status;

// --- CASE 1: A new project is starting --- //
if (status === 'initialise') {
  const mainId = $json.main_execution_id;
  const resumeUrl = $json.resume_url;
  const tasksToRun = $input.all();

  // Create a new section on our dashboard for this project.
  staticData.projects[mainId] = {
    resumeUrl: resumeUrl,
    tasks: {}
  };

  // Add each task to the dashboard with a 'pending' status and start time.
  for (const task of tasksToRun) {
    const processName = task.json.process;
    staticData.projects[mainId].tasks[processName] = {
      status: 'pending',
      startTime: new Date().toISOString()
    };
  }
  console.log(`Project ${mainId} initialized with ${tasksToRun.length} tasks.`);

  // Send the tasks out to the specialist teams.
  return tasksToRun;
}

// --- CASE 2: A specialist team has finished a task --- //
if (status === 'completed') {
  const mainId = $json.main_execution_id;
  const completedProcess = $json.process;
  const resultData = $json.data;
  const projectDashboard = staticData.projects[mainId];

  if (!projectDashboard) {
    console.log(`No dashboard found for mainId: ${mainId}.`);
    return []; // Stop if the project doesn't exist.
  }

  // Update the task on the dashboard.
  const task = projectDashboard.tasks[completedProcess];
  task.status = 'completed';
  task.endTime = new Date().toISOString();
  task.result = resultData;
  // Calculate duration in seconds
  const durationMs = new Date(task.endTime) - new Date(task.startTime);
  task.duration_seconds = parseFloat((durationMs / 1000).toFixed(2));

  console.log(`Task ${completedProcess} for project ${mainId} marked as completed in ${task.duration_seconds}s.`);

  // Check if ALL tasks on the dashboard are now 'completed'.
  const allDone = Object.values(projectDashboard.tasks).every(t => t.status === 'completed');

  if (allDone) {
    console.log(`All tasks for project ${mainId} are done. Resuming main workflow.`);
    const resumeUrl = projectDashboard.resumeUrl;
    const finalResponse = projectDashboard.tasks;

    // Clean up the dashboard for this project.
    delete staticData.projects[mainId];

    // Return a special item that will trigger the 'Resume Parent Workflow' node.
    return [{
      json: {
        end_process: true,
        resume_url: resumeUrl,
        response: finalResponse
      }
    }];
  }

  // If not all tasks are done, we do nothing and wait for the next team to report back.
  return [];
}

// By default, if the status is not 'initialise' or 'completed', do nothing.
return [];
