// Build BMKG API URL with error handling
try {
  const adm4 = $input.item(0).json.adm4;
  const urlTemplate = $input.item(0).json.bmkgUrlTemplate;

  if (!adm4 || !urlTemplate) {
    throw new Error('Missing required parameters: adm4 or urlTemplate');
  }

  const url = urlTemplate.replace('{{ADM4}}', adm4);

  return {
    json: { 
      url,
      timestamp: new Date().toISOString(),
      location: $input.item(0).json.locationName
    }
  };
} catch (error) {
  return {
    json: {
      error: error.message,
      url: 'https://api.bmkg.go.id/publik/prakiraan-weather?adm4=31.71.03.1001'
    }
  };
}

// Enhanced weather parsing with better error handling
try {
  const data = $input.item(0).json;
  const configData = $('Config').item(0).json;

  let rain_hours = 0, rain_prob_max6h = 0, rain_prob_avg12h = 0;
  let items = [];

  // Handle different API response structures
  if (data.data && Array.isArray(data.data)) {
    items = data.data;
  } else if (data.lokasi && data.lokasi[0] && data.lokasi[0].weather) {
    items = data.lokasi[0].weather;
  } else if (Array.isArray(data)) {
    items = data;
  }

  let rain_count = 0, rain_sum = 0, max6h = 0, total = 0;
  let weather_conditions = [];

  // Process weather data for next 12 hours (4 periods of 3 hours)
  for (let i = 0; i < Math.min(items.length, 4); i++) {
    const item = items[i];
    let rainProb = 0;

    // Extract rain probability from different possible fields
    if (item.hu) rainProb = parseFloat(item.hu) || 0;
    if (item.rain_prob) rainProb = parseFloat(item.rain_prob) || 0;
    if (item.weather && item.weather.toLowerCase().includes('rain')) rainProb = 80;
    if (item.weather_desc && item.weather_desc.toLowerCase().includes('rain')) rainProb = 75;

    // Estimate probability from weather description
    if (item.weather_desc) {
      const desc = item.weather_desc.toLowerCase();
      if (desc.includes('heavy rain') || desc.includes('thunderstorm')) rainProb = Math.max(rainProb, 85);
      else if (desc.includes('rain') && desc.includes('thunder')) rainProb = Math.max(rainProb, 80);
      else if (desc.includes('rain')) rainProb = Math.max(rainProb, 70);
      else if (desc.includes('clouds') && desc.includes('thick')) rainProb = Math.max(rainProb, 40);
    }

    rain_sum += rainProb;
    if (i < 2 && rainProb > max6h) max6h = rainProb; // First 6 hours (2 periods)
    if (rainProb > 0) rain_count++;
    total++;

    weather_conditions.push({
      period: i + 1,
      time: item.local_datetime || item.weatherTime || `Period ${i + 1}`,
      rain_prob: rainProb,
      weather: item.weather_desc || item.weather || 'N/A',
      temp: item.t || item.tempC || 'N/A'
    });
  }

  if (total > 0) {
    rain_prob_avg12h = Math.round(rain_sum / total);
    rain_prob_max6h = max6h;
  }

  const final_rain_prob = Math.max(rain_prob_avg12h, rain_prob_max6h);
  const threshold = parseInt(configData.thresholdProb) || 60;
  const feed_ratio = final_rain_prob >= threshold 
    ? parseInt(configData.reducePercent) || -20
    : 0;

  return {
    json: {
      rain_prob: final_rain_prob,
      rain_prob_max6h: rain_prob_max6h,
      rain_prob_avg12h: rain_prob_avg12h,
      feed_ratio: feed_ratio,
      weather_conditions: weather_conditions,
      analysis_time: new Date().toLocaleString('id-ID', { timeZone: 'Asia/Jakarta' }),
      location: configData.locationName
    }
  };

} catch (error) {
  return {
    json: {
      error: error.message,
      rain_prob: 0,
      rain_prob_max6h: 0,
      rain_prob_avg12h: 0,
      feed_ratio: 0,
      weather_conditions: [],
      analysis_time: new Date().toLocaleString('id-ID', { timeZone: 'Asia/Jakarta' })
    }
  };
}

// Log activity and prepare summary
const weatherData = $('Parse & Score Weather (6-12h)').item(0).json;
const feedData = $input.item(0).json;
const configData = $('Config').item(0).json;
const esp8266Response = $('ESP8266 Fish Feeder Control').item(0).json;

const logEntry = {
  timestamp: new Date().toISOString(),
  location: configData.locationName,
  rain_probability: weatherData.rain_prob,
  feed_ratio: feedData.feed_ratio,
  action_type: feedData.action_type,
  esp8266_status: esp8266Response ? 'success' : 'failed',
  weather_summary: weatherData.weather_conditions?.slice(0, 2) || [],
  bmkg_analysis_time: weatherData.analysis_time
};

return {
  json: logEntry
};
